mapper.select:
select id, owner, name, startDate, endDate, cost, description
from tbl_activity
where id = #{id}

select count(*) from tbl_activity
where id in

    #{id}

mapper.delete:
delete from tbl_activity
where id in

    #{id}

mapper.select:
select a.id, a.name, b.name as owner, a.startDate, a.endDate
from tbl_activity a
join tbl_user b
on a.owner = b.id


        a.name like '%' #{name} '%'


        and b.name like '%' #{owner} '%'


        and a.startDate >= #{startDate}


        and a.endDate <= #{endDate}


order by a.createTime desc
limit #{skipCount}, #{pageSize}

select count(*) from tbl_activity a join tbl_user b on a.owner = b.id
a.name like '%' #{name} '%'
and b.name like '%' #{owner} '%'
and a.startDate >= #{startDate}
and a.endDate <= #{endDate}
