mapper.select:
select count(*) from tbl_activity_remark
where activityId in

    #{id}

mapper.delete:
delete from tbl_activity_remark
where activityId in

    #{id}

mapper.select:
select * from tbl_activity_remark
where activityId = #{id}
order by editTime desc , createTime desc

mapper.update:
update tbl_activity_remark set
noteContent = #{noteContent},
editTime = #{editTime},
editBy = #{editBy},
editFlag = #{editFlag}
where id = #{id}
